import {
  Col,
  Row,
  Button,
  Card,
  CardBody,
  CardTitle,
  Table,
  ButtonGroup,
  Spinner,
  Alert,
} from "reactstrap";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { apis } from "../store/apis";

const tableHeaders = [
  { key: 1, header: "S/N" },
  { key: 2, header: "Names" },
  { key: 3, header: "Email" },
  { key: 4, header: "Age" },
  { key: 5, header: "Role" },
  { key: 6, header: "Actions" },
];

const Users = () => {
  const dispatch = useDispatch();
  const { users } = useSelector((state) => state.adminGetUsers);
  const { loading: deletingUser, message, error } = useSelector(
    (state) => state.deleteUser
  );

  const [showAlert, setShowAlert] = useState(false);

  useEffect(() => {
    dispatch(apis.getusers());
  }, [dispatch]);

  const handleDeleteUser = (userId) => {
    dispatch(apis.deleteUser(userId));
    // Re-fetch users after deletion
    dispatch(apis.getusers());
  };

  // Optionally hide success message after 3 seconds
  useEffect(() => {
    if (message) {
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 3000); // Auto-hide after 3 seconds
    }
  }, [message]);

  return (
    <div>
      {/* Table */}
      <Row>
        <Col lg="12">
          <Card>
            <CardBody>
              <CardTitle tag="h5">Users</CardTitle>
              {showAlert && message && (
                <Alert color="success">{message}</Alert>
              )}
              {error && <Alert color="danger">{error}</Alert>}
              <Table className="no-wrap mt-3 align-middle" responsive borderless>
                <thead>
                  <tr>
                    {tableHeaders.map((item) => (
                      <th key={item.key}>{item.header}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {users &&
                    users.map((user) => (
                      <tr key={user.id}>
                        <td>
                          <div className="d-flex align-items-center p-2">
                            <h6>{user.id}</h6>
                          </div>
                        </td>
                        <td>{user.name}</td>
                        <td>{user.email}</td>
                        <td>{user.age}</td>
                        <td>{user.role}</td>
                        <td>
                          <ButtonGroup>
                            {/* Optionally add an edit button here */}
                            <Button
                              onClick={() => handleDeleteUser(user.id)}
                              color="danger"
                              size="sm"
                              disabled={deletingUser}
                            >
                              {deletingUser ? (
                                <Spinner size="sm" />
                              ) : (
                                <i className="bi bi-trash-fill"></i>
                              )}
                            </Button>
                          </ButtonGroup>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </Table>
            </CardBody>
          </Card>
        </Col>
        <Col lg="12">
          <Button color="primary" className="mt-3">
            <Link className="text-decoration-none text-light" to="/dashboard/users/register">
              Add User
            </Link>
          </Button>
        </Col>
      </Row>
    </div>
  );
};

export default Users;
